# –û—Ç–ø—Ä–∞–≤–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏—è
async def send_message(bot, chat_id, message):
    try:
        await bot.send_message(chat_id=chat_id, text=message, parse_mode="Markdown")
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏—è: {e}")

# –û—Ç–ø—Ä–∞–≤–∫–∞ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–µ–Ω–Ω—ã—Ö —Å–æ–æ–±—â–µ–Ω–∏–π
async def send_welcome_messages(bot, chat_id):
    for message in MESSAGES["Start Day"]["welcome"]:
        await send_message(bot, chat_id, message)
    await send_message(bot, chat_id, MESSAGES["Start Day"]["instructions"])
    buttons = [
        [InlineKeyboardButton("–ó–∞–≥—Ä—É–∑–∏—Ç—å —Ñ–∞–π–ª", callback_data="download_file")],
        [InlineKeyboardButton("–ó–∞–≥—Ä—É–∑–∏—Ç—å –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ", callback_data="download_image")]
    ]
    await bot.send_message(chat_id=chat_id, text=MESSAGES["Start Day"]["file_buttons"], reply_markup=InlineKeyboardMarkup(buttons))

# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ä–∞—Å–ø–∏—Å–∞–Ω–Ω—ã—Ö —Å–æ–æ–±—â–µ–Ω–∏–π
async def schedule_task(bot, chat_id, day, time_of_day):
    messages = MESSAGES.get(day, {}).get(time_of_day, [])
    if not isinstance(messages, list):
        messages = [messages]

    for message in messages:
        await send_message(bot, chat_id, message)

# –ü–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ –∑–∞–¥–∞—á
# –ù–∞—á–∏–Ω–∞–µ–º –ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏–µ —Ç–æ–ª—å–∫–æ —Å–æ —Å–ª–µ–¥—É—é—â–µ–≥–æ –¥–Ω—è
def schedule_messages(bot, chat_id, day):
    if day in MESSAGES:
        schedule.every().day.at("08:00").do(lambda: asyncio.run(schedule_task(bot, chat_id, day, "morning")))
        schedule.every().day.at("13:00").do(lambda: asyncio.run(schedule_task(bot, chat_id, day, "afternoon")))
        schedule.every().day.at("21:00").do(lambda: asyncio.run(schedule_task(bot, chat_id, day, "evening")))

# –¶–∏–∫–ª –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫–∞
def run_scheduler():
    while True:
        schedule.run_pending()
        time.sleep(1)

# –ó–∞–ø—É—Å–∫ –ø–ª–∞–Ω–∏—Ä–æ–≤—â–∏–∫–∞
# –ü–ª–∞–Ω–∏—Ä—É–µ–º –∑–∞–¥–∞—á–∏ –Ω–∞—á–∏–Ω–∞—è —Å "Day 1" –∏ –æ—Ç–∫–ª–∞–¥—ã–≤–∞–µ–º –≤—ã–ø–æ–ª–Ω–µ–Ω–∏–µ –Ω–∞ 1 –¥–µ–Ω—å
def start_scheduled_messages(bot, chat_id):
    for day in range(1, 6):
        schedule.every(day).days.at("00:00").do(lambda: schedule_messages(bot, chat_id, f"Day {day}"))
    scheduler_thread = Thread(target=run_scheduler)
    scheduler_thread.daemon = True
    scheduler_thread.start()

# –°—Ç–∞—Ä—Ç–æ–≤–∞—è –∫–æ–º–∞–Ω–¥–∞
async def start(update: Update, context: CallbackContext):
    chat_id = update.message.chat_id
    await send_welcome_messages(context.bot, chat_id)
    start_scheduled_messages(context.bot, chat_id)

# –û—Å–Ω–æ–≤–Ω–æ–π –±–ª–æ–∫
if __name__ == "__main__":
    application = Application.builder().token(BOT_TOKEN).build()
    application.add_handler(CommandHandler("start", start))
    application.add_handler(CallbackQueryHandler(lambda update, context: context.bot.send_document(chat_id=update.callback_query.message.chat_id, document=open(file_path, 'rb')), pattern="download_file"))
    application.add_handler(CallbackQueryHandler(lambda update, context: context.bot.send_photo(chat_id=update.callback_query.message.chat_id, photo=open(image_path, 'rb')), pattern="download_image"))
    application.run_polling()
